	%macro	adi_	1
	add	ia,%1
	seto	byte [reg_fl]
	%endmacro
	extern	dvi__
	%macro	dvi_	1
	call	dvi__
	%endmacro

	extern	rmi__
	%macro	rmi_	1
	mov	rax,%1
