	%endmacro


	%macro	ino_	1
	mov	al,byte [reg_fl]
	or	al,al
	jno	%1
	%endmacro

	%macro	iov_	1
	mov	al,byte [reg_fl]
	or	al,al
	jo	%1
	%endmacro

	%macro	ldi_	1
	mov	ia,%1
	%endmacro

	%macro	mli_	1
	imul	ia,%1
	seto	byte [reg_fl]
	%endmacro

	%macro	ngi_	0
	neg	ia
	seto	byte [reg_fl]
	%endmacro

	extern	rti_
	%macro	rti_	0
	call	rti_
	mov	ia,m_word [reg_ia]
	%endmacro

	%macro	sbi_	1
	sub	ia,%1
	mov	rax,0
	seto	byte [reg_fl]
	%endmacro

	%macro	sti_	1
	mov	%1,ia
	%endmacro

;	code pointer instructions (cp maintained in location reg_cp)

	extern	reg_cp

	%macro	lcp_	1
	mov	rax,%1
	mov	m_word [reg_cp],rax
	%endmacro

	%macro	lcw_	1
	mov	rax,m_word [reg_cp]		; load address of code word
	mov	rax,m_word [rax]			; load code word
	mov	%1,rax
	mov	rax,m_word [reg_cp]		; load address of code word
	add	rax,cfp_b
	mov	m_word [reg_cp],rax
	%endmacro

	%macro	scp_	1
	mov	rax,m_word [reg_cp]
	mov	%1,rax
	%endmacro

	%macro	icp_	0
	mov	rax,m_word [reg_cp]
	add	rax,cfp_b
	mov	m_word [reg_cp],rax
	%endmacro

	%macro	rov_	1
	mov	al,byte [reg_fl]
	or	al,al
	jne	%1
	%endmacro

	%macro	rno_	1
	mov	al,byte [reg_fl]
	or	al,al
	je	%1
	%endmacro


